import React, {useState} from 'react'
import ToddoList from './ToddoList';



const Button = styled.button`

  width: 100%;
  font: inherit;
  padding: 0.5rem 1.5rem;
  border: 1px solid #8b005d;
  color: white;
  background: #8b005d;
  box-shadow: 0 0 4px rgba(0, 0, 0, 0.26);
  cursor: pointer;

  @media-query(min-width: 768px){

    width: auto;

  }

&:focus {
  outline: none;
}

&:hover,
&:active {
  background: #ac0e77;
  border-color: #ac0e77;
  box-shadow: 0 0 8px rgba(0, 0, 0, 0.26);
}

`;

function Toddo() {

    const [userInput, setUserInput] = useState("Enter Text");

    const[todoList, setTodoList] = useState([]);

    function changeText(e){
        setUserInput(e.target.value);
        console.log(e.target.value)

    }

    function handleSubmit(e){
        e.preventDefault();

        addUserInput(userInput);

        console.log(todoList);
        setUserInput("");
    }




    function addUserInput(userInput){
        let copy = [...todoList];
        copy.push(userInput)
        setTodoList(copy);


       console.log(copy)
    }





  return (
      <div>

         <section id="add-list">
          <form>
          <input placeholder = {userInput} onChange={changeText}
          ></input>
          </form>
          <Button onClick={handleSubmit}>Add Toddo</Button>
          </section>

          <section id="list">
          <ToddoList list = {todoList}/>
          </section>

     </div>

  )
}

export default Toddo
