import React, {useState} from 'react'
import ToddoList from './ToddoList';

import styled from 'styled-components';

import '../css/Toddo.css'



const Button = styled.button`

  width: 50%;
  font: inherit;
  padding: 0.5rem 1.5rem;
  border: 1px solid #8b005d;
  color: white;
  background: #8b005d;
  box-shadow: 0 0 4px rgba(0, 0, 0, 0.26);
  cursor: pointer;

  @media-query(min-width: 768px){

    width: auto;

  }

&:focus {
  outline: none;
}

&:hover,
&:active {
  background: #ac0e77;
  border-color: #ac0e77;
  box-shadow: 0 0 8px rgba(0, 0, 0, 0.26);
}

`;

const FormInput = styled.div`

margin: 0.5rem 0;


& label {
  font-weight: bold;
  display: block;
  margin-bottom: 0.5rem;
}



& input {
  display: block;
  width: 100%;
  border: 1px solid #ccc;
  font: inherit;
  line-height: 1.5rem;
  padding: 0 0.25rem;
}

& input:focus {
  outline: none;
  background: #fad0ec;
  border-color: #8b005d;
}

&.invalid input{
  border-color: red;
  background: #ffd7d7
}




&.invalid label{
  color: red;

}
`;

function Toddo() {

    const [userInput, setUserInput] = useState("Enter Text");

    const[todoList, setTodoList] = useState([]);

    function changeText(e){
        setUserInput(e.target.value);
        console.log(e.target.value)

    }

    function handleSubmit(e){
        e.preventDefault();

        addUserInput(userInput);

        console.log(todoList);
        setUserInput("");
    }




    function addUserInput(userInput){
        let copy = [...todoList];
        copy.push(userInput)
        setTodoList(copy);


       console.log(copy)
    }





  return (
      <div>

         <section id="add-list">
          <form>
              <FormInput>
                  <label>TODO LIST</label>
          <input placeholder = {userInput} onChange={changeText}
          ></input>
          </FormInput>
          </form>
          <Button onClick={handleSubmit}>Add Toddo</Button>
          </section>

          <section id="list">
          <ToddoList list = {todoList}/>
          </section>

     </div>

  )
}

export default Toddo
