        e.preventDefault();

        addUserInput(userInput);

        console.log(todoList);
        setUserInput("");
    }




    function addUserInput(userInput){
        let copy = [...todoList];
        copy.push(userInput)
        setTodoList(copy);


       console.log(copy)
    }





